import React, {useState} from 'react';
import styled from 'styled-components';

const InputCont = styled.div`
    display: flex;
    align-items: center;
    margin-top: 15px;
`;

const InputText = styled.h2`
    margin-right: 25px;
    font-family: sans-serif;
    font-size: 14px;
    line-height: 16px;
    color: ${props =>props.focused ?  "#FF0000" : "#686868"};

`;

const InputForm = styled.input`
    height: 40px;
    width: 300px;
    font-family: sans-serif;
    letter-spacing: 0.03em;
    background-color: #FFFFFF;
    ::placeholder{
        color: #000};
    border-radius: 5px;
    outline: none;
    border:solid ${props =>props.focused ? "#C4C4C4 ,1px" : "#FF0000, 2px"};
    padding-left: 10px;
`;

const Input = ({text, PHtext=text}) => {
    const[focused, setFocus] = useState(false);
    return <InputCont focused={focused}>
        <InputText>{text}Label</InputText>
        <InputForm onFocus={()=>{setFocus(true);}} type="text" placeholder={PHtext}></InputForm>
    </InputCont>;
}

export default Input;
