import React, {useState} from 'react';
import styled from 'styled-components';

const TextCont = styled.div`
    display:flex;
    flex-direction: column;
`;

const TextIn = styled.p`
font-family: sans-serif;
font-size: 16px;
margin-top: 0px;
color: ${props =>props.highlight ?  "#FFFFFF" : "#000000"};
background: ${props =>props.highlight ?  "#FF0000" : "none"};
cursor: pointer;
`;




const Text = ({}) => {
    const[highlight, setHighlight] = useState(false);
    return <TextCont onHover={()=>{setHighlight(true);}}>
        <TextIn highlight={highlight} >
            Highlighted text
        </TextIn>
    </TextCont>
}

export default Text;
