import React from 'react';
import styled from 'styled-components';

const InputCont = styled.div`
    display: flex;
    flex-direction: column;
    margin-top: 15px;
`;

const InputText = styled.h2`
    margin-bottom: 5px;
    font-family: sans-serif;
    font-size: 14px;
    line-height: 16px;
    color: #686868;

`;

const InputForm = styled.input`
    height: 40px;
    width: 300px;
    font-family: sans-serif;
    letter-spacing: 0.03em;
    background-color: #FFFFFF;
    ::placeholder{
        color: #000};
    border-radius: 5px;
    outline: none;
    border:1px solid ${props =>props.color ? props.color : "#C4C4C4"};
    padding-left: 10px;
`;

const Input = ({text, PHtext=text}) => {
    return <InputCont>
        <InputText>{text}Label</InputText>
        <InputForm type="text" placeholder={PHtext}></InputForm>
    </InputCont>;
}

export default Input;
