import React from 'react';
import styled, {css} from 'styled-components';

const ButtonContainer = styled.button`
    // Width: 310px;
    Height: 40px;
    background-color: ${props =>props.color ? props.color : "#4285F4"};
    border-radius: ${props => props.radius ? props.radius : "5px"};
    display: flex;
    justify-content: center;
    align-items: center;
    filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
    border:3px solid #002ED2;
    margin: 15px 0px 4px;
`;

const ButtonText = styled.p`
    text-align: center;
    font-size: 15px;
    color:${ props =>props.color ? props.color : "#FFF"};
`;

const Button = ({text,color,bgcolor, radius, width}) => {
    return <ButtonContainer color = {bgcolor} radius = {radius} width={width}>
                <ButtonText color = {color}>{text}Button</ButtonText>
            </ButtonContainer>
}

ButtonText.defaultprops = {
    color:null,
    text:"button",
}

export default Button;
