import React from 'react';
import logo from './google_logo.png';
import styled, {css} from 'styled-components';

const ButtonContainer = styled.button`
    Width: 300px;
    Height: 40px;
    background-color: ${props =>props.color ? props.color : "#4285F4"};
    border-radius: ${props => props.radius ? props.radius : "5px"};
    display: flex;
    justify-content: center;
    align-items: center;
    filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
    border:none;
    margin: 4px 0px 4px;
`;

const GoogleLogo = styled.div`
    ${({ google }) => !!google  && css`
        width:18px;
        height:18px;
        background-image: url(${logo});
        background-color:white;
        padding:10px;
        background-repeat: no-repeat;
        background-position: left;
        margin-top: 3px;
        `}
    `;

const ButtonText = styled.p`
    text-align: center;
    font-size: 15px;
    color:${ props =>props.color ? props.color : "#FFF"};
`;

const Button = ({text,color,bgcolor, radius, img}) => {
    return <ButtonContainer color = {bgcolor} radius = {radius}>
                <GoogleLogo img = {img}></GoogleLogo>
                <ButtonText color = {color}>{text}</ButtonText>
            </ButtonContainer>
}

ButtonText.defaultprops = {
    color:null,
}

export default Button;
