import React,{useState} from 'react';
import styled, {css} from 'styled-components';

const CheckCont = styled.div`
    display: flex;
    align-items: center;
    margin-top: 15px;
    height: 40px;
    width: 300px;
    font-family: sans-serif;
    background-color: #FFFFFF;
    border-radius: 5px;
    outline: none;
    border:solid 1px ${props =>props.checked ?  "#2C7AD5" : "#C4C4C4"};
    padding-left: 10px;
`;

const CheckBox = styled.input`
    background-color: ${props =>props.checked ?  "#8893B8" : "none"};
    border: 1px solid ${props =>props.checked ?  "#2C7AD5" : "#C4C4C4"};
    border-radius: 3px;
`;


const Check = () => {
    const[checked, setChecked] = useState(false);
    return <CheckCont checked={checked}>
        <CheckBox onClick={()=>{setChecked(true);}} type="checkbox"  />
    </CheckCont>
};

export default Check;
