import React , {useState} from 'react';
import styled, {css} from 'styled-components';

const ButtonContainer = styled.button`
    // Width: 310px;
    Height: 40px;
    background-color: ${props =>props.color ? props.color : "#4285F4"};
    border-radius: 5px;
    display: flex;
    justify-content: center;
    align-items: center;
    filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
    border:solid ${props =>props.disabled ?  "none" : "3px"} ${props =>props.disabled ?  "none" : "#C4C4C4"};;
    margin: 15px 0px 4px;
    padding: 0px 25px 0px 25px;
`;

const ButtonText = styled.p`
    text-align: center;
    font-size: 15px;
    color:${ props =>props.color ? props.color : "#FFF"};
`;

const Button = ({text,color,bgcolor}) => {
    const[disabled, setDisabled] = useState(false);
    return <ButtonContainer color = {bgcolor}>
                <ButtonText color = {color}>{text}Button</ButtonText>
            </ButtonContainer>
}

ButtonText.defaultprops = {
    color:null,
    text:"button",
}

export default Button;
