import React from 'react';
import styled from 'styled-components';
import hand from '../../Assets/hand.png';
import Button from '../'

const BubbleCont = styled.div`
    display: inline-flex;
    flex-direction: column;
    align-items: center;
    justify-content:space-around;
    padding-left:80px;
    padding-right:80px;
    padding-top: 25px;    
    background: #A1A6C5;
    border-radius: 5px;
    filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
    after {
        content: '';
        position: absolute;
        bottom: 0;
        left: 50%;
        width: 0;
        height: 0;
        border: 2px solid transparent;
        border-top-color: #A1A6C5;
        border-bottom: 0;
        border-left: 0;
        margin-left: -1px;
        margin-bottom: -2px;
    };
`;
const BubbleHeader = styled.h1`
font-family: sans-serif;
font-size: 28px;
color:${props => props.color ? props.color : "#FFF"};
`;
const BubbleText = styled.p`
text-align:center;
font-family: sans-serif;
font-size: 18px;
color:${props => props.color ? props.color : "#FFF"};
`;




const Bubble = ({ }) => {
    return <BubbleCont>
        <img src={hand} alt="icon" />
        <BubbleHeader>Welcome to My App!</BubbleHeader>
        <BubbleText>We're excited to help you<br/><br/>1/4</BubbleText>
    </BubbleCont>
}

export default Bubble;
