import React from 'react';
import styled from 'styled-components';
import hand from '../../Assets/hand.png';

const BubbleCont = styled.div`
    display: inline-flex;
    flex-direction: column;
    align-items: center;
    justify-content:center;    
    background: #A1A6C5;
    border-radius: 5px;
    filter: drop-shadow(0px 4px 4px rgba(0, 0, 0, 0.25));
    after {
        content: '';
        position: absolute;
        bottom: 0;
        left: 50%;
        width: 0;
        height: 0;
        border: 2px solid transparent;
        border-top-color: #A1A6C5;
        border-bottom: 0;
        border-left: 0;
        margin-left: -1px;
        margin-bottom: -2px;
    };
`;
const BubbleHeader = styled.h1`

`;
const BubbleText = styled.p`

`;




const Bubble = ({ }) => {
    return <BubbleCont>
        <img src={hand} alt="icon" />
        <BubbleHeader>Welcome to My App!</BubbleHeader>
        <BubbleText>We</BubbleText>
    </BubbleCont>
}

export default Bubble;
