import React from 'react';
import styled from 'styled-components';

const InputCont = styled.div`
    align-items: center;
    justify-content: center;
    input {display: inline-flex;
        align-items: center;
        justify-content: space-between;
        margin-top: 15px;}
    
`;

const InputText = styled.h2`
    margin-bottom: 5px;
    font-family: sans-serif;
    font-size: 14px;
    line-height: 16px;
    color: ${props=>props.color ? props.color : "#1E9761"};

`;

const InputForm = styled.input`
    font-family: sans-serif;
    text-align: center;
    background-color: #ECECEC;
    ::placeholder{
        color: #A5A5A5};
    border-radius: 20px;
    outline: none;
    border: none;
    padding-left: 10px;
`;

const Input = ({text, PHtext=text, color}) => {
    return <InputCont>
        <InputText>{text, color}</InputText>
        <InputForm type="text" placeholder={PHtext}></InputForm>
        <InputForm type="text" placeholder={PHtext}></InputForm>
        <InputForm type="text" placeholder={PHtext}></InputForm>
    </InputCont>;
}

export default Input;
