import React from 'react';
import styled, {css} from 'styled-components';

const Menutabcont = styled.div`
height: 84px;
width: 344px;
left: 0px;
top: 0px;
border-radius: 25px;
background: #FFFFFF;
border: 1px solid #E1E1E1;
box-sizing: border-box;
box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
cursor:pointer;
// display:flex;
margin:10px;
align-items:center;

`
const ContentContainer = styled.div`
display:flex; 

`

const TabIcon = styled.div`

`

const TabSquare = styled.div`
height: 42px;
width: 42px;
left: 15px;
top: 21px;
border-radius: 10px;
position:relative;
background: #FFFFFF;
box-shadow: -1px 3px 3px rgba(0, 0, 0, 0.25);
background-image:url(${props=>props.bgimg ? props.bgimg : "none"});

`

const TabText = styled.div`
position:relative;
text-align:left;
left:74px;
bottom:17px;
font-style: normal;
font-weight: 500;
font-size: 24px;
line-height: 36px;
`

const EnterIcon = styled.div`
align-items:right;
position:relative;
left:300px;
top:-50px;
`
const MenuTab = ({MenuText, bgimg, }) => {
    
    return <Menutabcont>
        <TabIcon>
                <TabSquare bgimg={bgimg}>
                {/* <img src="/currentorders.svg"/> */}
                </TabSquare>
            </TabIcon>
        
        <ContentContainer> 
            
            <TabText> {MenuText} </TabText>

            
            
        </ContentContainer>
        <EnterIcon>
            <img src="/enter.png"/>
            </EnterIcon> 
    </Menutabcont>

}

MenuTab.defaultProps = {
    
    MenuText:"Current Orders",
    bgimg : "./currentorders.svg"
}

export default MenuTab;
